const symbolInfo = ${symbolInfo};
const list= ${quotes};
const ans = [];

for (const oq of list) {
    const sbl = oq['symbol'];
    const name = symbolInfo[sbl]['name'];
    const q = oq['quotes'][0];
    open = q['quote']['USD']['open'];
    ans.push(genPointDto(sbl,name,'open',open,q));

    high = q['quote']['USD']['high'];
    ans.push(genPointDto(sbl,name,'high',high,q));

    low = q['quote']['USD']['low'];
    ans.push(genPointDto(sbl,name,'low',low,q));

    close = q['quote']['USD']['close'];
    ans.push(genPointDto(sbl,name,'close',close,q));    

    volume = q['quote']['USD']['volume'];
    ans.push(genPointDto(sbl,name,'volume',volume,q));       


    market_cap = q['quote']['USD']['market_cap'];
    ans.push(genPointDto(sbl,name,'market_cap',market_cap,q));            


}



function genPointDto(sbl,name,fd,v,q){
  const row = {
      measurement : 'daily',
      tags:{
        symbol : sbl,
        name : name,
        category:'cryptocurrency',
        valmean: fd  
      },
      field: 'val',
      val:v,
      time :q['quote']['USD']['timestamp']
  };
  return row;
}

ans;
