!return-json===
let toDelOds = ${toDelOds};
const lastPrice = ${binanceQuote.all.avgPrice};
const limitOds = ${limitOds.orders};
const takeProfitOds = ${takeProfitOds.orders};


function addFilterOds(ods){
    for (const od of ods) {
        let odPrice = $orderUtils.getPrice(od);
        let dp = Math.abs(odPrice - lastPrice);
        let r = dp / lastPrice;


        let lastD = new Date(od.updateAt);
        let checkAt = new Date();
        checkAt.setSeconds(checkAt.getSeconds() - 9*60*60*24);
         if (r>0.15 || lastD<checkAt ) {
             toDelOds.push(od);
         }

    }
}

addFilterOds(limitOds);
addFilterOds(takeProfitOds);

JSON.stringify(toDelOds);

