let queryDtos = [];

function genRealtimeKQuery(startCount){
    const startM = (startCount*5)+'m';
    return `
from(bucket: "quote")
  |> range(start: -`+startM+`)
  |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  ) 
  `;
}

function genRealtimeMaQuery(maCount5m){
    const startM = (maCount5m*5*2)+'m';
    return `
from(bucket: "quote")
  |> range(start: -`+startM+`)
  |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  )
  |> movingAverage(n: `+maCount5m+` )
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ r with _value: (r.low + r.open +r.close + r.high)/4.0 }))  
  `;

}

function genDayMaQuery(maCount1d){
    const startD = (maCount1d*3)+'d';
    return `
from(bucket: "quote")
  |> range(start: -`+startD+`)
  |> filter(fn: (r) =>
    r._measurement == "daily" and
    r.valmean == "open" and
    r.symbol == "${symbol}" 
  )
  |> movingAverage(n: `+maCount1d+` )
  `;
}

function genDifMaQuery(maCount,range,absed,absRate,last){
    const startM = (range*5)+'m';
    const absRateSyntax = absed ? '  |>map(fn: (r) => ({ r with _value: math.abs(x: r._value)*'+absRate+' }))  ':'';
    const lastSyntax = last ? '  |> last()' : '';
    return `
    import "math"
from(bucket: "quote")
  |> range(start: -`+startM+`)
 |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  )
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ r with _value: (r.low + r.open +r.close + r.high)/4.0 })) 
  |> difference()
  `+absRateSyntax+` 
  |> movingAverage(n: `+maCount+` ) 
    `+lastSyntax;
}

function genMACDQuery(fastCount,slowCount){
    const startM = (slowCount*5*5)+'m';
    return `
from(bucket: "quote")
  |> range(start: -`+startM+`)
  |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  )
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ r with _value: (r.low + r.open +r.close + r.high)/4.0 }))
  |> map(fn: (r) => ({ r with cp: r._value   }))
  |> movingAverage(n: `+fastCount+` )
  |> map(fn: (r) => ({ r with _fast: r.cp -r._value    }))
  |> map(fn: (r) => ({ r with _value: r.cp   }))
  |> movingAverage(n: `+slowCount+` )
  |> map(fn: (r) => ({ r with _slow: r.cp -r._value   }))
  |> map(fn: (r) => ({ r with _value: r._slow - r._fast   }))
  |> movingAverage(n: 9 )
    `;
}

function genRSIDIF(maCount){
    const startM = (maCount*5*4)+'m';
        return `
from(bucket: "quote")
  |> range(start: -`+startM+`)
  |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  )
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ r with _value: (r.low + r.open +r.close + r.high)/4.0 }))
  |> map(fn: (r) => ({ r with cp: r._value   }))
  |> difference()
  |> movingAverage(n: `+maCount+` ) 
  |> relativeStrengthIndex(n: `+maCount+` ) 
    `;
}

function genKRange(maCount){
    const startM = (maCount*5*2)+'m';
        return `
from(bucket: "quote")
  |> range(start: -`+startM+`)
  |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  )
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ r with _value: (  r.high-r.low ) }))  
  |> movingAverage(n: `+maCount+` ) 
    `;
}

function appendQueryDto(query,uid){
    const dto= {
        'query':query,
        'uid':uid
    };
    queryDtos.push(dto);
}

appendQueryDto(genRSIDIF(12*6),'rsiDif360m'); 
appendQueryDto(genRealtimeKQuery(12*6),'k5m'); 
appendQueryDto(genDifMaQuery(96,96*3,true,"4.0",true),'difTH'); 
appendQueryDto(genDifMaQuery(1,12*3,false,"-1.0",false),'dif'); 
appendQueryDto(genMACDQuery(2,72),'macd10m120m'); // macd10m120m
appendQueryDto(genKRange(12*6),'KRange360m'); // macd10m120m


JSON.stringify(queryDtos);

