const tsdbAvgs = $bzk.g('TSDBAvgs');
const tsdbAvgsMap = {};
function injectTSDBAvgs(){
    for (const e of tsdbAvgs) {
        console.log(e);
        const results = e.get('result');
        if(results.size()==1){
            const v =results.get(0).get('values');
            tsdbAvgsMap[e.get('uid')] = v
            $bzk.putObj('~cs.'+e.get('uid'),v);
        }else if(results.size()>=2){
            for(let i=0;i<results.size();i++){
                const v =results.get(i).get('values');
                const cols = results.get(i).get('columns');
                $bzk.putObj('~cs.'+e.get('uid')+'.'+cols.get('_field'),v);
            }
        }

    }

}

injectTSDBAvgs();

const rMap = ${y2Vals};



function setupWaitPicker(uid,waitCount,_rMap){
    const ops = {
        logicType:"MICRO",
        peakMaxWaitSeconds:waitCount*5*60
    };
    const ans= $tsFunc.findPeak(_rMap,ops);

    $bzk.putObj('~'+uid,ans);
}

function setupBigger(prefix,_rMap){
    const sortIso8601Keys = $tsFunc.sortIso8601Key(_rMap);

    const previousKey = sortIso8601Keys.get(1);
    const lastKey = sortIso8601Keys.get(0);


    $bzk.putObj('~'+prefix+'.previousMax',howBigger(_rMap,true,previousKey));
    $bzk.putObj('~'+prefix+'.previousMin',howBigger(_rMap,false,previousKey));
    $bzk.putObj('~'+prefix+'.lastMax',howBigger(_rMap,true,lastKey));
    $bzk.putObj('~'+prefix+'.lastMin',howBigger(_rMap,false,lastKey));
    $bzk.putObj('~'+prefix+'.previousVal',getMap(_rMap,previousKey));
    $bzk.putObj('~'+prefix+'.lastVal',getMap(_rMap,lastKey));

}

function getMap(_rMap,key){
    const ans = _rMap[key];
    if (ans!=null) return ans;
    return _rMap.get(key);
}

function howBigger(_rMap,big,key){
        const obj = {
            targetKey:key,
            bigger:big
        };
        return $tsFunc.howBigger(_rMap,JSON.stringify(obj));
}

setupWaitPicker('cs.difPeak',1,tsdbAvgsMap['dif']);
setupBigger('cs.priceBigger',rMap);
setupBigger('cs.difBigger',tsdbAvgsMap['dif']);

const KRange360m = tsdbAvgsMap['KRange360m'];
const lastKRange = $tsFunc.getLast(KRange360m);
$bzk.putObj('~cs.lastKRange',lastKRange);

true;

