!javascript===
let qs = [];
let h24q = `
from(bucket: "quote")
  |> range(start: -1d)
   |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" and
    r._field == "price"
  )
`;
let d7q = `
from(bucket: "quote")
  |> range(start: -7d,stop : -1d)
   |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" and
    r._field == "price"
  )
  |> window(every: 3h)
  |> mean()   
  |> duplicate(column: "_stop", as: "_time")
  |> window(every: inf)
`;
let y2q = `
import "date"
from(bucket: "quote")
  |> range(start: -2y, stop:-7d)
  |> filter(fn: (r) =>
    r._measurement == "daily" and
    r.symbol == "${symbol}"
  )
 |> map(fn: (r) => ({ r with _time: date.truncate(t: r._time, unit: 1d)    }))
 |> pivot(rowKey:["_time"], columnKey: ["valmean"], valueColumn: "_value")
 |> map(fn: (r) => ({ r with _value: (r.close + r.open + r.low + r.high)/4.00 })) 
`;

qs.push(h24q);
qs.push(d7q);
qs.push(y2q);
qs;
