let queryDtos = [];

function genRealtimeQuery(maCount5m){
    const startM = (maCount5m*5*2)+'m';
    return `
from(bucket: "quote")
  |> range(start: -`+startM+`)
  |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  )
  |> movingAverage(n: `+maCount5m+` )
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ r with _value: (r.low + r.open +r.close + r.high)/4.0 }))  
  `;

}

function genDayQuery(maCount1d){
    const startD = (maCount1d*3)+'d';
    return `
from(bucket: "quote")
  |> range(start: -`+startD+`)
  |> filter(fn: (r) =>
    r._measurement == "daily" and
    r.valmean == "open" and
    r.symbol == "${symbol}" 
  )
  |> movingAverage(n: `+maCount1d+` )
  `;
}

function genDifMaQuery(maRange){
    const startM = (maRange*5*3)+'m';
    return `
from(bucket: "quote")
  |> range(start: -`+startM+`)
 |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  )
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ r with _value: (r.low + r.open +r.close + r.high)/4.0 }))  
  |> difference()
  |> movingAverage(n: `+maRange+` )
    `;
}

function genMACDQuery(fastCount,slowCount){
    const startM = (slowCount*5*5)+'m';
    return `
from(bucket: "quote")
  |> range(start: -`+startM+`)
  |> filter(fn: (r) =>
    r._measurement == "realtime" and
    r.symbol == "${symbol}" 
  )
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({ r with _value: (r.low + r.open +r.close + r.high)/4.0 }))
  |> map(fn: (r) => ({ r with cp: r._value   }))
  |> movingAverage(n: `+fastCount+` )
  |> map(fn: (r) => ({ r with _fast: r.cp -r._value    }))
  |> map(fn: (r) => ({ r with _value: r.cp   }))
  |> movingAverage(n: `+slowCount+` )
  |> map(fn: (r) => ({ r with _slow: r.cp -r._value   }))
  |> map(fn: (r) => ({ r with _value: r._slow - r._fast   }))
  |> movingAverage(n: 9 )
    `;
}

function appendQueryDto(query,uid){
    const dto= {
        'query':query,
        'uid':uid
    };
    queryDtos.push(dto);
}


appendQueryDto(genRealtimeQuery(12),'m5x12'); // 1h
appendQueryDto(genRealtimeQuery(60),'m5x60'); // 5h
appendQueryDto(genDayQuery(5),'d5'); // 5d
appendQueryDto(genDifMaQuery(72),'difMa360m'); // difMa360m
appendQueryDto(genMACDQuery(2,12),'macd10m120m'); // macd10m120m


JSON.stringify(queryDtos);

