const tsdbAvgs = $bzk.g('TSDBAvgs');
const tsdbAvgsMap = {};
function injectTSDBAvgs(){

    for (const e of tsdbAvgs) {
        console.log(e);
        const v = e.get('result').get(0).get('values');
        tsdbAvgsMap[e.get('uid')] = v
        $bzk.putObj('~ma.'+e.get('uid'),v);
    }

}

injectTSDBAvgs();
const difMa360mMap = tsdbAvgsMap['difMa360m'];
const rMap = ${y2Vals};



//h8r1BiggerPeak start
const h8r1BiggerPeak = $tsFunc.findPeak(rMap, {
        persistTime:60*60*8,
        logicType:'BIGGER',
        reversePersistTime:60*60*0.85,
        reverseWaitTime:60*60*1
    });
$bzk.putObj('~h8r1BiggerPeak',h8r1BiggerPeak);

//last bigger



setupBigger('priceBigger',rMap);
setupBigger('difMa360mMapBigger',difMa360mMap);

function setupBigger(prefix,_rMap){
    const sortIso8601Keys = $tsFunc.sortIso8601Key(_rMap);

    const previousKey = sortIso8601Keys.get(1);

    $bzk.putObj('~'+prefix+'.previousMax',howBigger(_rMap,true,previousKey));
    $bzk.putObj('~'+prefix+'.previousMin',howBigger(_rMap,false,previousKey));
    $bzk.putObj('~'+prefix+'.previousVal',getMap(_rMap,previousKey));
    $bzk.putObj('~'+prefix+'.lastVal',getMap(_rMap,sortIso8601Keys.get(0)));

}

function getMap(_rMap,key){
    const ans = _rMap[key];
    if (ans!=null) return ans;
    return _rMap.get(key);
}

function howBigger(_rMap,big,key){
        const obj = {
            targetKey:key,
            bigger:big
        };
        return $tsFunc.howBigger(_rMap,JSON.stringify(obj));
}


