const difPeak = ${cs.difPeak};
const difPoints = $tsFunc.toPoints(${cs.dif});
const difTH = ${cs.difTH};
const difBigger = ${cs.difBigger};
const k5mLow = ${cs.k5m.low};
const k5mHigh = ${cs.k5m.high};

function getThVal(){
    var key;
    for (var k in difTH) {
        return difTH[k];
    }
}

const nearPeak = difPeak.trendInfo.nearPeak;



function getPositionSide(){
    return difBigger.lastVal > 0 ? 'LONG' : 'SHORT';
}

const positionSide = getPositionSide();


function findOverTHInfo(){
    let list = [];
    for(let i=1;i<difPoints.length;i++){
        const point = difPoints[i];
        list.push(point);
        if(positionSide == 'LONG' && point.val>0 ) return  {"finded":false , "msg":"rebound","list":list };
        if(positionSide == 'SHORT' && point.val<0 ) return  {"finded":false , "msg":"rebound","list":list };
        if(Math.abs(point.val)>getThVal()) return  {
            "finded":true ,
             "msg":"",
             "list":list ,
             "peak":point
        };
    }
    return  {"finded":false , "msg":"NO_FIND","list":list };
}

const difOverThInfo = findOverTHInfo();

function getStopPrice(){
    if(!difOverThInfo.hasOwnProperty('peak')) return -1;
    if(positionSide=='LONG'){
        return k5mLow[difOverThInfo.peak.key];
    }else if(positionSide == 'SHORT'){
        return k5mHigh[difOverThInfo.peak.key];
    }
    throw new Error('getStopPrice not support='+positionSide);
}

function genOrderObjJson(){
    const peakPrice=getStopPrice();
    return JSON.stringify({
        "targetPrice":peakPrice,
        "stopPrice": -1,
        "gapRate":-1,
        "attachName":'DifTrace',
        "attach":{
            "stopPrice":peakPrice
        }
    });
}

$bzk.putObj('positionSide',positionSide);
$bzk.put('difNearPeak',JSON.stringify( nearPeak));
$bzk.put('difOverThInfo',JSON.stringify( difOverThInfo));

$bzk.putObj('~thVal',getThVal());
$bzk.put('~orderLimitBody',genOrderObjJson());
true;

