const list= ${apiData};
const ans = [];





addBySide('sell',list['sell']);
addBySide('buy',list['buy']);


function addBySide(side,info){
    ans.push(genPointDto(side,'totalAmount',info['totalAmount'],info['lastAt']));
    ans.push(genPointDto(side,'avgPrice',info['avgPrice'],info['lastAt']));
    ans.push(genPointDto(side,'lastPrice',info['lastPrice'],info['lastAt']));
    ans.push(genPointDto(side,'highPrice',info['highPrice'],info['highPriceAt']));
    ans.push(genPointDto(side,'lowPrice',info['lowPrice'],info['lowPriceAt']));
    ans.push(genPointDto(side,'highAmount',info['highAmount'],info['highAmountAt']));
}


function genPointDto(side,fd,v,timeAt){
  const row = {
      measurement : 'realtime',
      tags:{
        symbol : "${symbol}",
        name : "${finName}",
        category:'cryptocurrency',
        source:'binance',
        side:side,
        trade:'60s'
      },
      field: fd,
      val:v,
      time :timeAt
  };
  return row;
}

ans;
